#include<stdio.h>
#define a printf
int main(){int y;a("年を西暦で入力してください\n");scanf("%d",&y);if(y>=1868&&y<=1912){a("明治%d年です\n",y-1868+1);}if(y>=1912&&y<=1926){a("大正%d年です\n",y-1912+1);}if(y>=1926&&y<=1989){a("昭和%d年です\n",y-1926+1);}if(y>=1989&&y<=2019){a("平成%d年です\n",y-1989+1);}if(y>=2019){a("令和%d年です\n",y-2019+1);};return 0;}